function likePost(){let e=$(this).attr("id").slice(3);$.ajax({type:"post",url:`/likes/toggle/?id=${e}&type=Post`,data:"",success:function(t){console.log(t),addOrRemoveLike(t,e)},error:function(e){console.log(e.responseText),new Noty({theme:"relax",text:"Error in liking post",type:"error",layout:"topCenter",timeout:1500}).show()}})}function addOrRemoveLike(e,t){let o=$(`#pld-${t}`),i=o.text().trim();e.data.deleted?"1"==i?o.text(""):o.text(""+(Number(i)-1)):""==i?o.text("1"):o.text(`${Number(i)+1}`)}function likeComment(){let e=$(this).attr("id").slice(3);$.ajax({type:"post",url:`/likes/toggle/?id=${e}&type=Comment`,data:"",success:function(t){console.log(t),addOrRemoveCommentLike(t,e)},error:function(e){console.log(e.responseText),new Noty({theme:"relax",text:"Error in liking comment",type:"error",layout:"topCenter",timeout:1500}).show()}})}function addOrRemoveCommentLike(e,t){let o=$(`#cld-${t}`),i=o.text().trim();console.log(e),e.data.deleted?"1"==i?o.text(""):o.text(""+(Number(i)-1)):""==i?o.text("1"):o.text(`${Number(i)+1}`)}$(".post-like").on("click",likePost),$(".comment-like").on("click",likeComment);